import React, { Component } from 'react'

export class GoogleAuth extends Component {
  state = { isSignedIn: null };

  componentDidMount() {
      window.gapi.load("client:auth2", () => {
          window.gapi.client.init({ // Initializing
              clientId: "588976460688-rq8sjcgdktvi7l21ej1e6nc2a45c3uv7.apps.googleusercontent.com",
              scope: "email"
          }).then(() => {
            this.auth = window.gapi.auth2.getAuthInstance() // Getting the Reference to the Auth Library or Objects
            this.setState({ isSignedIn: this.auth.isSignedIn.get() }) // Updating our component level State
            this.auth.isSignedIn.listen(this.onAuthChange);
          });
      });
    }

    onAuthChange = () => { // This is a CallBack function, Make sure you make it an arrow function so it is Bind to the component
      this.setState({ isSignedIn: this.auth.isSignedIn.get() });
    }

  renderAuthButton() {  // Helper Function
      if (this.state.isSignedIn === null) {
          return null;
      }else if (this.state.isSignedIn) {
          return (
            <button className="ui red google button">
              <i className="google icon" />
            </button>
          );
      }else {
          return (
            <button className="ui red google button">
                <i className="google icon"/>
            </button>
          );
      }
    }

  render() {
    return (
      <div>
        {this.renderAuthButton()}
      </div>
    );
  }
}

export default GoogleAuth;
